import React, { useRef, useEffect, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";

const socket = io("http://localhost:5000");

function App() {
  const myVideo = useRef();
  const userVideo = useRef();
  const [stream, setStream] = useState(null);
  const [hasConnected, setHasConnected] = useState(false);
  const [peer, setPeer] = useState(null);

  useEffect(() => {
    console.log("ðŸš€ Connecting to backend...");

    // Get user's media stream first
    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then(currentStream => {
      console.log("âœ… Got local stream");
      setStream(currentStream);
      if (myVideo.current) {
        myVideo.current.srcObject = currentStream;
      }

      // Wait until stream is set before listening for socket events
      socket.on("connect", () => {
        console.log("ðŸ§  Socket connected:", socket.id);
        if (!hasConnected) {
          setupSocketEvents(currentStream);
          setHasConnected(true);
        }
      });
    }).catch(err => {
      console.error("âŒ Error getting user media:", err);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const setupSocketEvents = (currentStream) => {
    socket.on("partner", (partnerId) => {
      console.log("ðŸ‘¥ Matched with partner:", partnerId);

      const newPeer = new Peer({
        initiator: true,
        trickle: false,
        stream: currentStream,
      });

      newPeer.on("signal", data => {
        console.log("ðŸ“¤ Sending signal to partner");
        socket.emit("signal", { to: partnerId, data });
      });

      newPeer.on("stream", partnerStream => {
        console.log("ðŸ“¥ Received stream from partner");
        if (userVideo.current) {
          userVideo.current.srcObject = partnerStream;
        }
      });

      socket.on("signal", ({ data }) => {
        console.log("ðŸ“© Signal received by initiator");
        newPeer.signal(data);
      });

      setPeer(newPeer);
    });

    socket.on("signal", ({ from, data }) => {
      console.log("ðŸ“© Signal received by receiver:", from);

      const newPeer = new Peer({
        initiator: false,
        trickle: false,
        stream: currentStream,
      });

      newPeer.on("signal", signalData => {
        console.log("ðŸ“¤ Responding to signal from initiator");
        socket.emit("signal", { to: from, data: signalData });
      });

      newPeer.on("stream", partnerStream => {
        console.log("ðŸ“¥ Received stream from initiator");
        if (userVideo.current) {
          userVideo.current.srcObject = partnerStream;
        }
      });

      newPeer.signal(data);
      setPeer(newPeer);
    });
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Omegle Clone ðŸš€</h1>
      <div style={{ display: "flex", justifyContent: "center", gap: "20px" }}>
        <div>
          <h3>You</h3>
          <video ref={myVideo} autoPlay playsInline muted style={{ width: "300px", border: "1px solid black" }} />
        </div>
        <div>
          <h3>Stranger</h3>
          <video ref={userVideo} autoPlay playsInline style={{ width: "300px", border: "1px solid black" }} />
        </div>
      </div>
    </div>
  );
}

export default App;
